WatchmenTest MyAppOVF {
    Notifies {
        InfluxDB {
            url 'http://influxdb.mydomain.tld'
            port 8086
            Database watchmen
            MeasurementName watchmen_test
            tags @{
                prop1 = 'asdf'
                prop2 = 'qwerty'
                prop3 = 42
            }
        } -when 'always'
    }
}

WatchmenOptions {
    Notifies {
        Email @{
            FromAddress = 'watchmen@mydomain.tld'
            SmtpServer = 'smtp.mydomain.tld'
            Port = 25
            Subject = 'Watchmen Alert - #{computername} -[#{test}] failed!'
        }
        EventLog @{
            EventId = 1
            EventType = 'error'
        }
        FileSystem '\\fileserver01.mydomain.tld\monitoringshare\#{computername}.log'
        PowerShell {
            Write-Host "Something bad happended! $args[0]"
        }
        PowerShell '\notifier.ps1'
        Slack @{
            Title = 'Watchmen Bot'
            Token = '<webhookurl>'
            Channel = '#watchmen'
            EconEmoji = ':fire:'
        }
        Syslog 'syslog.mydomain.tld'
    }
}

